import React from 'react';
import ReactDOM from 'react-dom';
import './App.css';

export default function Items(prop) {

    return (
        <div className="card border-info col-6 col-md-4 col-lg-4" >
            <img className="rounded" src={process.env.PUBLIC_URL + prop.img} alt="Card image cap"  height="200px" witdh="100%"/>
                <div className="card-body text-center">
                    <p className="card-text">{prop.name} </p>
                    <a href="#" class="btn btn-sm btn-primary">भाव आणि माहिती </a>
                </div>
</div>

        )
}
